import React from 'react';
import { Button, NativeBaseProvider, Box, Input, FormControl, VStack} from 'native-base';


// Navegacion de Inicio
export const LogIn = ({navigation}) => {
    const [data, setData] = React.useState({});
    const [errors, setError] = React.useState({});
    const handleLogin = () => {
        //Validar
        console.log("validando")
        if (data.user === undefined || data.user === ''){
            setError({...errors,user:'Ingrese un usuario!'})
            console.log('USER ERROR')
            return false;
        }   
        if (data.pass === undefined || data.pass === ''){
            setError({...errors,pass:'Ingrese una contraseÃ±a!'})
            console.log('PASS ERROR')
            return false;
        }
        //Peticion HTTP
        console.log(data.user,data.pass)
    }

    const Blogin = () => {
        return <Button onPress={handleLogin}>Entrar</Button>
    }
    const BsignIn = () => {
        return <Button onPress={() => navigation.navigate('singin')}>No tengo cuenta</Button>
    }
    return(
        <NativeBaseProvider>
            <Box>
                <VStack>
                    <FormControl isRequired isInvalid={'user' in errors}>
                        <FormControl.Label>Nombre de Usuario</FormControl.Label>
                        <Input 
                            placeholder='Usuario'
                            onChangeText={(value) => setData({...data, user:value})}
                        />
                        {'user' in errors ?
                        <FormControl.ErrorMessage _text={{fontSize: 'xs', color: 'error.500', fontWeight: 500}}>Necesario usuario</FormControl.ErrorMessage>:
                            <FormControl.HelperText _text={{fontSize: 'xs'}}>Diferenciar MAYUS de MINUS</FormControl.HelperText>
                        }
                    </FormControl>
                </VStack>
                <VStack>
                    <FormControl isRequired isInvalid={'pass' in errors}>
                        <FormControl.Label>ContraseÃ±a</FormControl.Label>
                        <Input 
                            placeholder='ContraseÃ±a'
                            type = 'password'
                            onChangeText={(value) => setData({...data, pass:value})}
                        />
                        {'pass' in errors ?
                        <FormControl.ErrorMessage _text={{fontSize: 'xs', color: 'error.500', fontWeight: 500}}>Se necesita contraseÃ±a</FormControl.ErrorMessage>:
                            <FormControl.HelperText _text={{fontSize: 'xs'}}>Diferenciar MAYUS de MINUS</FormControl.HelperText>
                        }
                    </FormControl>
                </VStack>
                <Blogin/>
            </Box>
            <Box>
                <BsignIn/>
            </Box>
        </NativeBaseProvider>
    );
}

export const SingIn = ({navigation}) => {
    const [user, setUser] = React.useState('');
    const [pass, setPass] = React.useState('');
    const [cpass, setCpass] = React.useState('');
    const [name, setName] = React.useState('');
    const [address, setAddress] = React.useState('');
    const [tlp, setTlp] = React.useState(0);
    const [errors, setError] = React.useState({})
    const validar = (data,type) => {
        //Campo vacio
        if (data.length == 0 ) {
            setError("Campo vacio")
            return false;
        }
        //Espacio vacios
        var espacios = false;
        var cont = 0;

        while (!espacios && (cont < data.length)) {
        if (data.charAt(cont) == " ")
            espacios = true;
        cont++;
        }   
        if (espacios) {

        return false;
        }
    }
    const Iuser = () => {
        return (
            <VStack><FormControl isRequired>
                <FormControl.Label>Nombre de Usuario</FormControl.Label>
                <Input 
                    p={2} 
                    placeholder='Usuario'
                    onChangeText={(value) => setUser(value)}
                />
                {'user' in errors ?
                <FormControl.ErrorMessage _text={{fontSize: 'xs', color: 'error.500', fontWeight: 500}}>{errors['user']}</FormControl.ErrorMessage>:
                    <FormControl.HelperText _text={{fontSize: 'xs'}}>Diferenciar MAYUS de MINUS</FormControl.HelperText>
                }
            </FormControl></VStack>
        );
    }
    const Ipass = () => {
        return (
            <VStack><FormControl isRequired>
                <FormControl.Label>ContraseÃ±a</FormControl.Label>
                <Input 
                    p={2} 
                    placeholder='ContraseÃ±a'
                    onChangeText={(value) => setPass(value)}
                    type = 'password'
                />
                <Input
                    p={2}
                    placeholder='Confirmar contraseÃ±a'
                    onChangeText={(value)=> setCpass(value)}
                    type = 'password'
                />
                {'pass' in errors ?
                <FormControl.ErrorMessage _text={{fontSize: 'xs', color: 'error.500', fontWeight: 500}}>{errors['pass']}</FormControl.ErrorMessage>:
                    <FormControl.HelperText _text={{fontSize: 'xs'}}>Diferenciar MAYUS de MINUS</FormControl.HelperText>
                }
            </FormControl></VStack>
        );
    }
    const Iname = () => {
        return (
            <VStack><FormControl isRequired>
                <FormControl.Label>Nombre Completo</FormControl.Label>
                <Input 
                    p={2} 
                    placeholder='Nombre'
                    onChangeText={(value) => setName(value)}
                />
                {'name' in errors ?
                <FormControl.ErrorMessage _text={{fontSize: 'xs', color: 'error.500', fontWeight: 500}}>{errors['name']}</FormControl.ErrorMessage>:
                    <FormControl.HelperText _text={{fontSize: 'xs'}}>Escriba nombre completo</FormControl.HelperText>
                }
            </FormControl></VStack>
        );
    }
    const Iaddress = () => {
        return(
        <VStack><FormControl isRequired>
            <FormControl.Label>Domicilio</FormControl.Label>
            <Input 
                p={2} 
                placeholder='Domicilio'
                onChangeText={(value) => setAddress(value)}
            />

            {'address' in errors ?
