        );
        const msj = await response.text();
        if (parseInt(msj) > 0){
            return true;
        }else{
            return false;
        }
                
    } catch (error) {
        console.log('falta',error);
        return false;
    }
}
export const deleteVacuna = async (tkn,vacuna) => {
    try {
