                        }
                    </FormControl>
                </Modal.Body>
                <Modal.Footer>
                    <Button.Group space={2}>
                        <Button variant="ghost" colorScheme="blueGray" size = 'md' onPress={() => {
                        setShowModal(false);
                        }}>
                            Cancelar
                        </Button >
                        <Button onPress={() => {
                        setShowModal(false);
                        }}>
                            Verificar
                        </Button>
                    </Button.Group>
                </Modal.Footer>
            </Modal.Content>
        </Modal>
        </ScrollView>
        </KeyboardAvoidingView>
    );
}
